<span class="line">    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>CommonUtils<span class="token punctuation">.</span><span class="token function">reverseString</span><span class="token punctuation">(</span><span class="token string">'abcd'</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token comment">// dcba</span></span>
<span class="line">    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>CommonUtils<span class="token punctuation">.</span><span class="token function">palindrome</span><span class="token punctuation">(</span><span class="token string">'abcb'</span><span class="token punctuation">)</span><span class="token punctuation">,</span> CommonUtils<span class="token punctuation">.</span><span class="token function">palindrome</span><span class="token punctuation">(</span><span class="token string">'abcba'</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token comment">// false true</span></span>
<span class="line">    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>CommonUtils<span class="token punctuation">.</span><span class="token function">truncate</span><span class="token punctuation">(</span><span class="token string">'boomerang'</span><span class="token punctuation">,</span> <span class="token number">7</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token comment">// boomera...</span></span>
<span class="line"></span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span></span>
